mapper.select:
/*  EventMapper.findEventList  */
select
     event_id
    ,group_id
    ,FN_DATE_FORMAT(event_date) as event_date
    ,event_content
    ,writer
    ,FN_DATE_FORMAT(create_date) as create_date
    ,FN_DATE_FORMAT(update_date) as update_date
 from ch_event

    group_id=#{groupId}

        and DATE_FORMAT(event_date, '%Y%m%d') >=  #{fromDate}


        and DATE_FORMAT(event_date, '%Y%m%d') <=   #{toDate}


    order by event_date desc
LIMIT #{offset}, #{limit}

mapper.insert:
/*  EventMapper.createEvent  */
insert into ch_event (group_id, event_content, writer, event_date, create_user)
VALUES
(
    #{groupId},
    #{eventContent},
    #{writer},
    STR_TO_DATE(#{eventDate}, '%Y%m%d'),
    #{loginUserId}
)

mapper.update:
/*  EventMapper.updateEvent  */
update ch_event
set
  event_content = #{eventContent}
, writer = #{writer}
, update_user = #{loginUserId}
where event_id = #{eventId}

mapper.delete:
/*  EventMapper.removeEvent  */
delete from ch_event
where event_id = #{value}

mapper.select:
/*  EventMapper.getEventByGroup  */
select * from ch_event
where event_date = #{eventDate}
and group_id = #{groupId}
