mapper.select:
/*  EventMapper.findEventList  */
select * from ch_event

    group_id=#{groupId}

        and CAST(event_date AS CHARACTER) >=  CONCAT( #{fromDate}, ' 00:00:00')


        and CAST(event_date AS CHARACTER) <=  CONCAT( #{toDate}, ' 23:59:59')


LIMIT #{offset}, #{limit}

/*  EventMapper.getEvent  */
select * from ch_event
where event_id = #{value}

mapper.insert:
/*  EventMapper.createEvent  */
insert into ch_event (group_id, event_content, writer, event_date)
VALUES
(
    #{groupId},
    #{eventContent},
    #{writer},
    #{eventDate}
)

mapper.update:
/*  EventMapper.updateEvent  */
update ch_event
set
event_content = #{eventContent}
, group_id = #{groupId}
, writer = #{writer} 
, event_date = #{eventDate} 
where event_id = #{eventId}

mapper.delete:
/*  EventMapper.removeEvent  */
delete from ch_event
where event_id = #{value}
