mapper.select:
/*  MenuMapper.findMenuChildList  */
select * from ch_menu
where parent_menu_id = #{value}

/*  MenuMapper.getMenu  */
select * from ch_menu
where menu_id = #{value}

mapper.update:
/*  MenuMapper.updateMenu  */
update ch_menu
set
 update_date = now()
,update_user = #{loginUserId}
,menu_name = #{menuName}
,menu_url = #{menuUrl}
, parent_menu_id = #{parentMenuId}
, menu_level = #{menuLevel} 
, sort_idx = #{sortIdx} 
where menu_id = #{menuId}

mapper.delete:
/*  MenuMapper.removeMenu  */
delete from ch_menu
where menu_id = #{value}
