mapper.select:
/* PastureStoryMapper.findStoryList */ select A.story_id ,A.user_id ,A.group_id ,FN_DATE_FORMAT(A.input_date) as input_date ,A.worship_yn ,A.leader_yn ,A.pasture_meet_yn ,A.bible_count ,A.qt_count ,A.friday_worship_yn ,A.dawn_pray_count ,A.etc ,FN_DATE_FORMAT(A.create_date) as create_date ,FN_DATE_FORMAT(A.update_date) as update_date ,B.master_id ,B.prayers ,B.worship_reason ,FN_CODE_DESC('WORSHIP_REASON', B.worship_reason) as worship_desc ,B.leader_reason ,FN_CODE_DESC('LEADER_REASON', B.leader_reason) as leader_desc from ch_story_master A inner join ch_story_pasture B on A.story_id = B.master_id
where A.user_id=#{userId}
and DATE_FORMAT(A.input_date, '%Y%m%d') >= #{fromDate}
and DATE_FORMAT(A.input_date, '%Y%m%d') <= #{toDate}
order by A.input_date desc
LIMIT #{offset}, #{limit}

/*  PastureStoryMapper.findStoryListCnt  */
select
count(A.story_id)  AS cnt
from ch_story_master A
inner join ch_story_pasture B on A.story_id = B.master_id

where A.user_id=#{userId}

    and DATE_FORMAT(A.input_date, '%Y%m%d') >=  #{fromDate}


    and DATE_FORMAT(A.input_date, '%Y%m%d') <=   #{toDate}

mapper.update:
/*  PastureStoryMapper.updateStory  */
update ch_story_master
set
  update_date = now()
, worship_yn = #{worshipYn}
,leader_yn = #{leaderYn}
, pasture_meet_yn = #{pastureMeetYn}
, bible_count = #{bibleCount}
, qt_count = #{qtCount}
, friday_worship_yn = #{fridayWorshipYn}
, dawn_pray_count = #{dawnPrayCount}
, etc = #{etc}


where story_id = #{storyId};

update ch_story_pasture
set
    prayers = #{prayers}
  , worship_reason = #{worshipReason}
  , leader_reason = #{leaderReason}
  , update_user = #{loginUserId}
where master_id = #{storyId};

mapper.delete:
/*  PastureStoryMapper.removeStory  */
delete from ch_story_master
where story_id = #{value};

delete from ch_story_pasture
where master_id = #{value};
